import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
// import { useNavigate } from 'react-router-dom';
import { useCart } from 'react-use-cart';
import './ProductInfo.css'

function ProductInfo(props) {
    const [singleProduct, setSingleProduct] = useState('')
    // const navigate = useNavigate();
    const { addItem } = useCart();

    const id = useParams().id;


    const sendRequest = async () => {
        const res = await axios
          .get(`http://localhost:5000/api/products/${id}`)
          .catch((err) => console.log(err));
        const data = await res.data;
        console.log(data);
        return data;
      };
      useEffect(() => {
        sendRequest().then((data) => setSingleProduct(data.product));
      }, []);
      console.log(singleProduct);
    
  
   {singleProduct && singleProduct.product.map(()=>{ return(
    <div className="cardComponent col-11 col-md-6 col-lg-3 text-center ">
    <div className="card shadow">
      <div className="card-image">
        <img  src={props.image} className="card-img-top" alt="..." />
      </div>
      <div className="card-body ">
        <h5 className="card-title">{props.title}</h5><br />
        <p className="card-text description">{props.description}</p>
        <h5 className="card-text price ">Rs.{props.price}</h5>
        <button
          className="btn btn-primary c"
          onClick={() => addItem(props.item)}
        >
          Add to cart
        </button>
      </div>
    </div>
  </div>
   )
   
   }) }
       
   
        
 
}

export default ProductInfo
